import React, { useEffect, useState } from 'react'
import './main.css'

const Chat = ({socket , name, room } )=>{
    const [ currentMessage , setCurrentMessage] = useState("");
    const [ mesList , setmesList] = useState([]);

    const sendMessage = async ()=>{
        if(currentMessage !== ''){
            const messageData={
                room:room,
                author:name,
                message:currentMessage,
                //time:new Date(Date.now()).getHours() + ":" + new Date(Date.now()).getMinutes()
            };
            await socket.emit('send_message',messageData);
        }
        //setCurrentMessage("")
    };

    useEffect(()=>{socket.on("recived_message",(data)=>{
        setmesList((list)=>{[...list,data]})
    })}, [socket]);

    return(
        <>
            <div className='main_div'>
                <div className='cent_div'>
                    <div className='header'><h2>Live chat</h2></div>
                    <div className='body'></div>
                    <div className='footer'>
                        <input className='inp' type='text' placeholder='hey..'onChange={(event)=>{setCurrentMessage(event.target.value)}}/>
                        <button onClick={sendMessage}>&#9658;</button>
                    </div>
                </div>
            </div>
        </>
    )
}
export default Chat;
